CREATE OR REPLACE PROCEDURE estorna_canc_cot (nr_cot_compra_P bigint, ds_titulo_p text, ds_historico_p text, ie_origem_p text, nm_usuario_p text) AS $body$
BEGIN
update	cot_compra
set	nr_seq_motivo_cancel = ''
where	nr_cot_compra = nr_cot_compra_P;

update	cot_compra_forn
set	ie_status = ''
where	nr_cot_compra = nr_cot_compra_P
and	ie_status = 'CH';

CALL gerar_historico_cotacao( nr_cot_compra_p,
			 ds_titulo_p,
			 ds_historico_p,
			 ie_origem_p,
			 nm_usuario_p);
commit;
end;
$body$
