SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estorna_map_devolucao (nr_devolucao_p bigint, nr_item_devolucao_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_map_w	bigint;
nr_sequencia_w	bigint;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
				

BEGIN 
 
if (coalesce(nr_atendimento_p::text, '') = '') then 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	devolucao_material_pac 
	where	nr_devolucao = nr_devolucao_p;
end if;
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_seq_map_w 
from	material_atend_paciente 
where	nr_devolucao = nr_devolucao_p 
and	nr_seq_item_devol = nr_item_devolucao_p 
and 	nr_atendimento = coalesce(nr_atendimento_p, nr_atendimento_w) 
and	cd_acao = 2;
 
if (nr_seq_map_w > 0) then 
 
	select	nextval('material_atend_paciente_seq') 
	into STRICT	nr_sequencia_w         
	;
 
	insert into material_atend_paciente( 
		nr_sequencia, 
		nr_atendimento, 
		dt_entrada_unidade, 
		cd_material, 
		dt_atendimento, 
		cd_unidade_medida, 
		qt_material, 
		dt_atualizacao, 
		nm_usuario, 
		cd_convenio, 
		cd_categoria, 
		dt_prescricao, 
		cd_material_prescricao, 
		ie_via_aplicacao, 
		vl_material, 
		cd_tab_preco_material, 
		dt_vigencia_tabela, 
		nr_prescricao, 
		nr_sequencia_prescricao, 
		cd_acao, 
		cd_setor_atendimento, 
		qt_devolvida, 
		cd_motivo_devolucao, 
		nr_cirurgia, 
		nr_doc_convenio, 
		qt_executada, 
		dt_conta, 
		vl_unitario, 
		cd_proced_referencia, 
		nr_aih, 
		ie_valor_informado, 
		nr_seq_autorizacao, 
		cd_material_exec, 
		nr_seq_atepacu, 
		cd_cgc_fornecedor, 
		ie_auditoria, 
		cd_local_estoque, 
		nr_seq_cor_exec, 
		nr_seq_aih, 
		nr_seq_lote_fornec, 
		nr_devolucao, 
		nr_seq_item_devol) 
	SELECT	nr_sequencia_w, 
		nr_atendimento, 
		dt_entrada_unidade, 
		cd_material, 
		clock_timestamp(), 
		cd_unidade_medida, 
		(qt_material * -1), 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_convenio, 
		cd_categoria, 
		dt_prescricao, 
		cd_material_prescricao, 
		ie_via_aplicacao, 
		(qt_material * vl_unitario * -1), 
		cd_tab_preco_material, 
		dt_vigencia_tabela, 
		nr_prescricao, 
		nr_sequencia_prescricao, 
		1, 
		cd_setor_atendimento, 
		qt_material, 
		'', 
		nr_cirurgia, 
		nr_doc_convenio, 
		0, 
		dt_conta, 
		vl_unitario, 
		cd_proced_referencia, 
		nr_aih, 
		'N', 
		nr_seq_autorizacao, 
		cd_material_exec, 
		nr_seq_atepacu, 
		cd_cgc_fornecedor, 
		ie_auditoria, 
		null, 
		0, 
		nr_seq_aih, 
		nr_seq_lote_fornec, 
		nr_devolucao, 
		nr_seq_item_devol 
	from	material_atend_paciente 
	where	nr_sequencia = nr_seq_map_w;
end if;
 
CALL atualiza_preco_material(nr_sequencia_w, nm_usuario_p);
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
