SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE estorna_utilizacao_kit_estoque ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
qt_existe_w		bigint;
ie_consiste_baixa_prescr_w	varchar(1);


BEGIN
if (obter_funcao_ativa = 143) then
	ie_consiste_baixa_prescr_w := obter_param_usuario(143, 253, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_consiste_baixa_prescr_w);
else
	ie_consiste_baixa_prescr_w := obter_param_usuario(50, 57, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_consiste_baixa_prescr_w);
end if;

if (ie_consiste_baixa_prescr_w = 'S') then
	begin
	select	coalesce(max(nr_prescricao),0)
	into STRICT	nr_prescricao_w
	from	kit_estoque
	where	nr_sequencia	= nr_sequencia_p;

	if (nr_prescricao_w > 0) then
		begin
		select	count(*)
		into STRICT	qt_existe_w
		from	prescr_material
		where	(dt_baixa IS NOT NULL AND dt_baixa::text <> '')
		and	nr_seq_kit_estoque = nr_sequencia_p
		and	nr_prescricao = nr_prescricao_w;

		if (qt_existe_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(265987);
			--'A utilização deste kit estoque não pode ser estornada pois já possui itens baixados na prescrição ' || nr_prescricao_w;
		end if;
		end;
	end if;
	end;
end if;

update	kit_estoque
set	dt_utilizacao	= '',
	nm_usuario_util	= '',
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	nr_atendimento	 = NULL,
	nr_prescricao	 = NULL
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
