CREATE OR REPLACE PROCEDURE est_lib_perfil_usuario ( cd_setor_usuario_p bigint, cd_perfil_liberar_p bigint, cd_setor_a_liberar_p bigint, nm_usuario_p text) AS $body$
DECLARE



nm_usuario_w	varchar(15);
ie_contador_w	bigint;
C01 CURSOR FOR
	SELECT 	distinct(a.nm_usuario)
	from	usuario a,
		usuario_perfil b
	where	a.nm_usuario = b.nm_usuario
	and	b.cd_perfil  = cd_perfil_liberar_p
	and (a.cd_setor_atendimento = coalesce(cd_setor_usuario_p,0) or coalesce(cd_setor_usuario_p,0) = 0);


BEGIN

if (coalesce(cd_perfil_liberar_p,0) > 0) and (coalesce(cd_setor_a_liberar_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select 	count(*)
		into STRICT	ie_contador_w
		from	usuario_setor
		where	cd_setor_atendimento = cd_setor_a_liberar_p
		and	nm_usuario_param = nm_usuario_w;

		if (ie_contador_w = 0) then

			insert into usuario_setor(
						nm_usuario_param,
						cd_setor_atendimento,
						dt_atualizacao,
						nm_usuario)
			values (
						nm_usuario_w,
						cd_setor_a_liberar_p,
						clock_timestamp(),
						nm_usuario_p);


		end if;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
