CREATE OR REPLACE PROCEDURE esus_atual_num_aval_alteradas (nr_sequencia_p esus_atividade_coletiva.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_aval_alter_w		bigint := 0;


BEGIN

begin
select	count(1)
into STRICT	qt_aval_alter_w
from	esus_ativ_col_partic
where	coalesce(ie_avaliacao_alterada,'X') = 'S'
and	nr_seq_ativ_colet = nr_sequencia_p;
exception
when others then
	qt_aval_alter_w := 0;
end;

update	esus_atividade_coletiva
set 	nr_avaliacao_alterad = qt_aval_alter_w,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
