CREATE OR REPLACE PROCEDURE esus_gerar_proced_pac_prof ( cd_profissional_p text, cd_cbo_p text, nr_atendimento_p bigint, dt_atendimento_p timestamp, nr_seq_ficha_p bigint, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

					 
cd_procedimento_w		esus_ficha_pro_proced.cd_procedimento%type;
ie_origem_proced_w		esus_ficha_pro_proced.ie_origem_proced%type;
nr_interno_conta_w			conta_paciente.nr_interno_conta%type := 0;
qt_proc_gerado_w			bigint := 0;

C01 CURSOR FOR 
	SELECT	b.cd_procedimento, 
		b.ie_origem_proced 
	from	procedimento_paciente b 
	where	trunc(b.dt_procedimento) = trunc(dt_atendimento_p) 
	and	coalesce(b.cd_pessoa_fisica,b.cd_medico_executor) = cd_profissional_p 
	and	b.cd_cbo = cd_cbo_p 
	and	b.nr_atendimento = nr_atendimento_p 
	and	((nr_interno_conta_w = 0) or (b.nr_interno_conta = nr_interno_conta_w)) 
	and	coalesce(b.cd_motivo_exc_conta::text, '') = '' 
	group by	b.cd_procedimento, 
		b.ie_origem_proced;				
					 

BEGIN 
 
begin 
select 	coalesce(min(nr_interno_conta),0) 
into STRICT	nr_interno_conta_w 
from	conta_paciente 
where 	nr_atendimento = nr_atendimento_p 
and	trunc(dt_atendimento_p) between trunc(dt_periodo_inicial) and trunc(dt_periodo_final);
exception 
when others then 
	nr_interno_conta_w := 0;
end;
 
open C01;
loop 
fetch C01 into	 
	cd_procedimento_w, 
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	count(1) 
	into STRICT	qt_proc_gerado_w 
	from	esus_ficha_pro_proced 
	where	nr_seq_ficha_proc = nr_seq_ficha_p 
	and	cd_procedimento	= cd_procedimento_w 
	and	ie_origem_proced = ie_origem_proced_w;
	 
	if (qt_proc_gerado_w = 0) then 
		begin 
		 
		insert into esus_ficha_pro_proced( 
			nm_usuario, 
			nm_usuario_nrec, 
			dt_atualizacao, 
			dt_atualizacao_nrec, 
			cd_procedimento, 
			ie_origem_proced, 
			nr_seq_ficha_proc, 
			nr_sequencia) 
		values (	nm_usuario_p, 
			nm_usuario_p, 
			clock_timestamp(), 
			clock_timestamp(), 
			cd_procedimento_w, 
			ie_origem_proced_w, 
			nr_seq_ficha_p, 
			nextval('esus_ficha_pro_proced_seq'));
			 
		end;
	end if;
	 
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
