CREATE OR REPLACE PROCEDURE esus_inserir_retirar_lote ( nr_sequencia_p text, nm_tabela_p text, nr_seq_lote_p text, ie_acao_p text, nm_usuario_p text, ie_retorno_p INOUT text ) AS $body$
DECLARE


ie_status_lote_w varchar(1);
sql_update_w varchar(255);


BEGIN

ie_retorno_p := '';
select	ie_status_lote
into STRICT	ie_status_lote_w
from 	esus_lote_envio
where 	nr_sequencia = nr_seq_lote_p;
if (ie_acao_p = 'R') then
begin
	if (ie_status_lote_w = 'A') then
	begin
		sql_update_w := 'update ' || nm_tabela_p || ' set nr_seq_lote_envio = null, nm_usuario = ''' || nm_usuario_p || ''', dt_atualizacao = sysdate where nr_sequencia = ' || nr_sequencia_p;
	end;
	else
		ie_retorno_p := wheb_mensagem_pck.get_texto(381878);
	end if;
end;
else
	sql_update_w := 'update ' || nm_tabela_p || ' set nr_seq_lote_envio = ' || nr_seq_lote_p || ', nm_usuario = ''' || nm_usuario_p || ''', dt_atualizacao = sysdate where nr_sequencia = ' || nr_sequencia_p;
end if;

if (coalesce(ie_retorno_p,'X') = 'X') then
	CALL EXEC_SQL_DINAMICO(nm_usuario_p,sql_update_w);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
