CREATE OR REPLACE PROCEDURE esus_mudar_status_lote (nr_sequencia_p text, nm_usuario_p text, ie_retorno_p INOUT text ) AS $body$
DECLARE


dt_envio_w timestamp;
ie_status_lote_w varchar(1);


BEGIN

select  dt_envio_lote,
	ie_status_lote
into STRICT 	dt_envio_w,
	ie_status_lote_w
from esus_lote_envio
where nr_sequencia = nr_sequencia_p;


if (coalesce(dt_envio_w::text, '') = '') then
	begin
	if (ie_status_lote_w = 'A') then
		update esus_lote_envio set ie_status_lote = 'F', nm_usuario = nm_usuario_p, dt_atualizacao = clock_timestamp() where nr_sequencia = nr_sequencia_p;
	else
		update esus_lote_envio set ie_status_lote = 'A', nm_usuario = nm_usuario_p, dt_atualizacao = clock_timestamp() where nr_sequencia = nr_sequencia_p;
	end if;
	commit;
	ie_retorno_p := '';
	end;
else
	ie_retorno_p := wheb_mensagem_pck.get_texto(381879);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
