CREATE OR REPLACE PROCEDURE eup_atualizar_tipo_convenio ( nr_atendimento_p bigint, cd_convenio_p bigint) AS $body$
DECLARE

 
ie_atualizar_tipo_conv_atend_w	varchar(1) := 'S';
ie_tipo_convenio_w		smallint  := 2;


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(cd_convenio_p,0) > 0) then 
	begin 
 
	select	coalesce(max(ie_atualizar_tipo_conv_atend),'S') 
	into STRICT	ie_atualizar_tipo_conv_atend_w 
	from	parametro_faturamento 
	where	cd_estabelecimento = obter_estabelecimento_ativo;
	 
	if (ie_atualizar_tipo_conv_atend_w = 'S') then 
		begin 
		 
		select	max(ie_tipo_convenio) 
		into STRICT	ie_tipo_convenio_w 
		from	convenio 
		where	cd_convenio = cd_convenio_p;
		 
		if (coalesce(ie_tipo_convenio_w,0) > 0) then 
		 
			update	atendimento_paciente 
			set	ie_tipo_convenio = ie_tipo_convenio_w 
			where	nr_atendimento 	 = nr_atendimento_p;
 
			commit;
		end if;
		end;
	end if;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
