SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eup_grava_valor_da_consulta (nr_atendimento_p bigint, vl_consulta_p bigint) AS $body$
DECLARE

 
ie_existe_atend_w	varchar(1);
vl_consulta_w		varchar(13);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_existe_atend_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
		 
		if (ie_existe_atend_w = 'N') then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(176886);
		end if;	
	 
	exception 
		when others then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(176893);
	end;
 
	begin		 
		update	atendimento_paciente set 
			vl_consulta	= vl_consulta_p, 
			nm_usuario	= wheb_usuario_pck.get_nm_usuario, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_atendimento	= nr_atendimento_p;		
		commit;
 
	exception 
		when others then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(176896);
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
