SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eup_obter_complemento_cep_js ( cd_cep_p text, ds_municipio_p INOUT text, cd_estado_p INOUT text, cd_cep_ret_p INOUT text, ds_bairro_p INOUT text, nr_logradouro_p INOUT text, cd_municipio_ibge_p INOUT text) AS $body$
DECLARE




ds_complemento_w	varchar(2000);
ie_cep_internet_w	varchar(15);
nm_logradouro_w		varchar(125);
nm_localidade_w		varchar(60);
nm_bairro_w		varchar(80);
sg_estado_w		valor_dominio.vl_dominio%type;
cd_cep_w		varchar(8);
cd_municipio_ibge_w	varchar(6);
cd_cep_p_w		bigint;


c01 CURSOR FOR
SELECT 	a.nm_logradouro,
	a.nm_localidade,
	a.nm_bairro,
	a.cd_unidade_federacao,
	a.nr_logradouro
from 	cep_logradouro a
where 	a.cd_logradouro = cd_cep_p
order by
	a.dt_atualizacao;

c02 CURSOR FOR
SELECT 	a.nm_logradouro,
	b.nm_localidade,
	c.ds_bairro,
	a.ds_uf,
	a.cd_cep
from 	cep_loc b,
	cep_bairro c,
	cep_log a
where  	b.nr_sequencia      	= c.nr_seq_loc
and   	a.cd_bairro_inicial	= c.nr_sequencia
and   	b.nr_sequencia      	= a.nr_seq_loc
and   	a.cd_cep            	= cd_cep_p
order by
	a.dt_atualizacao;

c03 CURSOR FOR
SELECT 	null,
	b.nm_localidade,
	null,
	b.ds_uf,
	b.cd_cep
from   	cep_loc b
where  	b.cd_cep = cd_cep_p
order by
	b.dt_atualizacao;


BEGIN
if (cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') then
	begin
	cd_cep_p_w := somente_numero(cd_cep_p);

	ie_cep_internet_w := Obter_Param_Usuario(0, 25, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_cep_internet_w);

	if (ie_cep_internet_w = 'N') then
		begin
		open c01;
		loop
		fetch c01 into	nm_logradouro_w,
				nm_localidade_w,
				nm_bairro_w,
				sg_estado_w,
				cd_cep_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			nm_logradouro_w	:= nm_logradouro_w;
			nm_localidade_w	:= nm_localidade_w;
			nm_bairro_w	:= nm_bairro_w;
			sg_estado_w	:= sg_estado_w;
			cd_cep_w	:= cd_cep_w;
			end;
		end loop;
		close c01;
		end;
	else
		begin
		open c02;
		loop
		fetch c02 into	nm_logradouro_w,
				nm_localidade_w,
				nm_bairro_w,
				sg_estado_w,
				cd_cep_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			nm_logradouro_w	:= nm_logradouro_w;
			nm_localidade_w	:= nm_localidade_w;
			nm_bairro_w	:= nm_bairro_w;
			sg_estado_w	:= sg_estado_w;
			cd_cep_w	:= cd_cep_w;
			end;
		end loop;
		close c02;
		end;
	end if;

	if (coalesce(cd_cep_w::text, '') = '') then
		begin
		open c03;
		loop
		fetch c03 into	nm_logradouro_w,
				nm_localidade_w,
				nm_bairro_w,
				sg_estado_w,
				cd_cep_w;
		EXIT WHEN NOT FOUND; /* apply on c03 */
			begin
			nm_logradouro_w	:= nm_logradouro_w;
			nm_localidade_w	:= nm_localidade_w;
			nm_bairro_w	:= nm_bairro_w;
			sg_estado_w	:= sg_estado_w;
			cd_cep_w	:= cd_cep_w;
			end;
		end loop;
		close c03;
		end;
	end if;

	cd_municipio_ibge_w 	:= substr(obter_municipio_ibge(cd_cep_p_w),1,6);

	--ds_complemento_w	:= nm_localidade_w || ';' || sg_estado_w || ';' || nm_logradouro_w || ';' || nm_bairro_w || ';' || cd_municipio_ibge_w;
	ds_municipio_p 	:= nm_localidade_w;
	cd_estado_p    	:= sg_estado_w;
	cd_cep_ret_p	:= nm_logradouro_w;
	ds_bairro_p	:= nm_bairro_w;
	cd_municipio_ibge_p := cd_municipio_ibge_w;
	nr_logradouro_p	:= substr(obter_dados_cep(cd_cep_p_w,'CTL'),1,3);

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eup_obter_complemento_cep_js ( cd_cep_p text, ds_municipio_p INOUT text, cd_estado_p INOUT text, cd_cep_ret_p INOUT text, ds_bairro_p INOUT text, nr_logradouro_p INOUT text, cd_municipio_ibge_p INOUT text) FROM PUBLIC;

