CREATE OR REPLACE PROCEDURE eup_obter_inf_alta_int_tiss_js ( ie_tipo_evento_p text, nr_atendimento_p bigint, nm_usuario_p text, nr_sequencia_p INOUT bigint, cd_convenio_p INOUT bigint) AS $body$
DECLARE

 
ie_tipo_convenio_w	smallint;					
cd_convenio_w		integer;
nr_seq_xml_projeto_w	bigint;
qt_internacao_w		bigint;
cd_estabelecimento_w	smallint;
nr_sequencia_w		bigint;
				

BEGIN 
 
cd_estabelecimento_w:= wheb_usuario_pck.get_cd_estabelecimento;
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	max(a.cd_convenio), 
		max(c.ie_tipo_convenio) 
	into STRICT	cd_convenio_w, 
		ie_tipo_convenio_w 
	from 	atend_categoria_convenio a, 
		atendimento_paciente b, 
		convenio c 
	where  a.nr_atendimento = b.nr_atendimento 
	and 	a.nr_seq_interno = obter_atecaco_atendimento(a.nr_atendimento) 
	and   a.cd_convenio = c.cd_convenio 
	and 	b.nr_atendimento = nr_atendimento_p;
 
	if (coalesce(cd_convenio_w,0) > 0) then 
		nr_seq_xml_projeto_w := TISS_OBTER_XML_PROJETO(cd_convenio_w, cd_estabelecimento_w,20);
	end if;
	if (ie_tipo_evento_p = 'I') then 
		select	count(*) 
		into STRICT	qt_internacao_w 
		from 	tiss_comunic_benif 
		where nr_atendimento = nr_atendimento_p 
		and ie_tipo_evento = 'I';
	end if;
	 
	if (ie_tipo_convenio_w = 2) and (coalesce(nr_seq_xml_projeto_w,0) <> 0) and 
		((coalesce(qt_internacao_w,0) = 0) or (ie_tipo_evento_p = 'A')) then 
		nr_sequencia_w := tiss_gerar_comunic_benef(cd_estabelecimento_w, nr_atendimento_p, nm_usuario_p, ie_tipo_evento_p, null, clock_timestamp(), nr_sequencia_w);
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
			nr_sequencia_p := coalesce(nr_sequencia_w,0);
			cd_convenio_p := cd_convenio_w;
		end if;
	end if;
	 
end if;
 
commit;
 
end;
