CREATE OR REPLACE PROCEDURE eup_obter_orient_exame_js ( cd_procedimento_p bigint, cd_exame_p text, nr_seq_proc_interno_p text, nm_usuario_p text, ds_orientacao_p INOUT text) AS $body$
DECLARE


ds_orientacao_w	varchar(4000);
nr_seq_exame_w	bigint;


BEGIN

begin
if (cd_exame_p IS NOT NULL AND cd_exame_p::text <> '') then

		begin
			select  coalesce(nr_seq_exame,0)
			into STRICT	nr_seq_exame_w
			from    exame_laboratorio
			where   upper(cd_exame)   = cd_exame_p
			and     lab_obter_se_exame_lib(nr_seq_exame, wheb_usuario_pck.get_cd_estabelecimento) = 'S'
			and     ie_solicitacao    = 'S';
		exception
		when others then
			nr_seq_exame_w := 0;
		end;

	if (coalesce(nr_seq_exame_w,0) > 0) then
		ds_orientacao_w := substr(obter_orientacao_exame(nr_seq_exame_w, null, null, null),1,2000);
		ds_orientacao_w := substr(ds_orientacao_w || substr(obter_orientacao_exame(nr_seq_exame_w,null, null, null),2001,4000),1,4000);
	end if;

elsif (coalesce(cd_procedimento_p,0) > 0) and (coalesce(nr_seq_proc_interno_p,0) > 0) then

	ds_orientacao_w := substr(obter_orientacao_proced_pac(cd_procedimento_p,0,nr_seq_proc_interno_p),1,4000);
end if;

ds_orientacao_p := ds_orientacao_w;

exception
when others then
ds_orientacao_p := '';
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
