SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ev_ajustar_dt_evento () AS $body$
DECLARE



C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_evento
	from	ev_evento_paciente
	order by 1;

nr_sequencia_w	bigint;
dt_evento_w	timestamp;
qt_reg_w	bigint;


BEGIN

qt_reg_w := 0;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	dt_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_reg_w := qt_reg_w + 1;

	UPDATE	ev_evento_pac_destino
	SET	dt_evento = dt_evento_w
	WHERE	nr_seq_ev_pac = nr_sequencia_w;

	IF (qt_reg_w >= 10000) THEN
		commit;
		qt_reg_w := 0;
	END IF;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ev_ajustar_dt_evento () FROM PUBLIC;

