CREATE OR REPLACE PROCEDURE exames_escala_framingham_lab (nr_atendimento_p bigint, qt_result_exam_hdl_p INOUT bigint, qt_result_exam_col_p INOUT bigint, qt_pa_sistolica_p INOUT bigint) AS $body$
DECLARE

 
cd_estabelecimento_w	smallint;
nr_seq_exame_hdh_w	bigint;
nr_seq_exame_col_w	bigint;
qt_result_exam_hdh_w	double precision;
qt_result_exam_col_w	double precision;
qt_pa_sistolica_w	smallint;
dt_aprovacao_w		timestamp;

c01 CURSOR FOR 
	SELECT 	b.qt_resultado, 
		b.dt_aprovacao dt_aprovacao 
	from 	prescr_medica c, 
		exame_lab_result_item b, 
		exame_lab_resultado a, 
		prescr_procedimento x 
	where	a.nr_seq_resultado = b.nr_seq_resultado 
	and	a.nr_prescricao = c.nr_prescricao 
	and	x.nr_sequencia = b.nr_seq_prescr 
	and	x.nr_prescricao = c.nr_prescricao 
	and	b.nr_seq_exame = nr_seq_exame_hdh_w 
	and	c.nr_atendimento = nr_atendimento_p 
	and	x.ie_status_atend >= 35 
	and	(b.qt_resultado IS NOT NULL AND b.qt_resultado::text <> '') 
	
union all
 
	SELECT 	b.qt_resultado, 
		coalesce(b.dt_aprovacao,a.dt_liberacao) dt_aprovacao 
	from 	exame_lab_result_item b, 
		exame_lab_resultado a		 
	where	a.nr_seq_resultado = b.nr_seq_resultado 
	and	coalesce(a.nr_prescricao::text, '') = '' 
	and	b.nr_seq_exame = nr_seq_exame_hdh_w 
	and	a.nr_atendimento = nr_atendimento_p 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and	ie_status = '1' 
	and	(b.qt_resultado IS NOT NULL AND b.qt_resultado::text <> '')	 
	order by dt_aprovacao;

c02 CURSOR FOR 
	SELECT 	b.qt_resultado, 
		b.dt_aprovacao dt_aprovacao 
	from 	prescr_medica c, 
		exame_lab_result_item b, 
		exame_lab_resultado a, 
		prescr_procedimento x 
	where	a.nr_seq_resultado = b.nr_seq_resultado 
	and	a.nr_prescricao = c.nr_prescricao 
	and	x.nr_sequencia = b.nr_seq_prescr 
	and	x.nr_prescricao = c.nr_prescricao 
	and	b.nr_seq_exame = nr_seq_exame_col_w 
	and	c.nr_atendimento = nr_atendimento_p 
	and	x.ie_status_atend >= 35 
	and	(b.qt_resultado IS NOT NULL AND b.qt_resultado::text <> '') 
	
union all
 
	SELECT 	b.qt_resultado, 
		coalesce(b.dt_aprovacao,a.dt_liberacao) dt_aprovacao 
	from 	exame_lab_result_item b, 
		exame_lab_resultado a 
	where	a.nr_seq_resultado = b.nr_seq_resultado 
	and	coalesce(a.nr_prescricao::text, '') = '' 
	and	b.nr_seq_exame = nr_seq_exame_col_w 
	and	a.nr_atendimento = nr_atendimento_p 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and	(b.qt_resultado IS NOT NULL AND b.qt_resultado::text <> '') 
	and	ie_status = '1' 
	order by dt_aprovacao;

c03 CURSOR FOR 
	SELECT	qt_pa_sistolica 
	from	atendimento_sinal_vital 
	where	nr_atendimento = nr_atendimento_p 
	and	(qt_pa_sistolica IS NOT NULL AND qt_pa_sistolica::text <> '') 
	order by 
		dt_sinal_vital;
	

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
end if;
 
if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then 
	begin 
	select	max(nr_seq_exame_hdh), 
		max(nr_seq_exame_col) 
	into STRICT	nr_seq_exame_hdh_w, 
		nr_seq_exame_col_w 
	from	escala_framingham_lab 
	where	cd_estabelecimento = cd_estabelecimento_w;
	exception 
	when others then 
		nr_seq_exame_hdh_w:= null;
		nr_seq_exame_col_w:= null;
	end;
end if;
 
open c01;
loop 
fetch c01 into 
	qt_result_exam_hdh_w, 
	dt_aprovacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
qt_result_exam_hdl_p:= qt_result_exam_hdh_w;
 
end loop;
close c01;
 
open c02;
loop 
fetch c02 into 
	qt_result_exam_col_w, 
	dt_aprovacao_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
 
qt_result_exam_col_p:= qt_result_exam_col_w;
 
end loop;
close c02;
 
open c03;
loop 
fetch c03 into 
	qt_pa_sistolica_w;
EXIT WHEN NOT FOUND; /* apply on c03 */
 
qt_pa_sistolica_p:= qt_pa_sistolica_w;
 
end loop;
close c03;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
