CREATE OR REPLACE PROCEDURE exame_inserir_alteracao_reg ( dt_alteracao_p timestamp, ds_alteracao_p text, nr_seq_exame_p bigint, cd_exame_integ_p text, nm_usuario_p text, nr_seq_externo_p bigint, nr_seq_log_alter_p INOUT bigint, cd_erro_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_exame_w		bigint;
cd_equipamento_w	equipamento_lab.cd_equipamento%type;


BEGIN

cd_erro_p	:= 0;

CALL tasy_atualizar_dados_sessao(nr_seq_externo_p);

if (coalesce(dt_alteracao_p::text, '') = '') then
	cd_erro_p	:= 26;
else

	select	max(a.cd_equipamento)
	into STRICT	cd_equipamento_w
	from	equipamento_lab a
	where	a.ds_sigla = 'TLAB';

	if (coalesce(cd_equipamento_w::text, '') = '') then
		cd_erro_p	:= 16;
	else

		if (coalesce(nr_seq_exame_p,0) > 0) then
			nr_seq_exame_w	:= nr_seq_exame_p;
		else
			select	max(a.nr_seq_exame)
			into STRICT	nr_seq_exame_w
			from  	lab_exame_equip a
			where	a.cd_equipamento = cd_equipamento_w
			and	a.cd_exame_equip = cd_exame_integ_p;
		end if;

		/*if	(nvl(nr_seq_exame_w,0) = 0) then
			cd_erro_p	:= 18;
		else*/
		begin

		select	nextval('exame_laboratorio_altera_seq')
		into STRICT	nr_seq_log_alter_p
		;

		insert into exame_laboratorio_altera(
								nr_sequencia,
								ds_alteracao,
								dt_vigor,
								nr_seq_exame,
								dt_atualizacao,
								dt_atualizacao_nrec,
								nm_usuario,
								nm_usuario_nrec)
						values (
								nr_seq_log_alter_p,
								ds_alteracao_p,
								dt_alteracao_p,
								nr_seq_exame_w,
								clock_timestamp(),
								clock_timestamp(),
								nm_usuario_p,
								nm_usuario_p);
		--OS727249 - Ivan
		--commit;
		exception
		when others then
			cd_erro_p	:= 1;
			ds_erro_p	:= substr(wheb_mensagem_pck.get_texto(805388,'DS_ERRO='||sqlerrm),1,2000);
		end;
		--end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
