SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_adiantamentos (cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_adiantamento_w 	bigint;
qt_dias_w		smallint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
SELECT 	a.nr_adiantamento
from	adiantamento a
where	not exists (select	1
	from	movto_trans_financ b
	where	a.nr_adiantamento	= b.nr_adiantamento)
and	not exists (select	1
	from	titulo_receber_liq c
	where	a.nr_adiantamento	= c.nr_adiantamento)
and	not exists (select 1
	from	adiantamento_cheque_cr d
	where	a.nr_adiantamento	= d.nr_adiantamento)
and	not exists (select	1
	from	movto_cartao_cr e
	where	a.nr_adiantamento	= e.nr_adiantamento)
and	not exists (select	1
	from	conta_paciente_adiant f
	where	a.nr_adiantamento	= f.nr_adiantamento)
and	not exists (select	1
	from	movto_banco_pend_baixa g
	where	a.nr_adiantamento	= g.nr_adiantamento)
and	a.vl_saldo		> 0
and	a.vl_adiantamento	= a.vl_saldo
and	coalesce(a.nr_seq_caixa_rec::text, '') = ''
and	a.cd_estabelecimento	= cd_estabelecimento_w
and	a.dt_adiantamento	<= clock_timestamp() - qt_dias_w;

c02 CURSOR FOR
SELECT	a.cd_estabelecimento
from	estabelecimento a
where	coalesce(a.ie_situacao,'A')	= 'A';


BEGIN

open	c02;
loop
fetch	c02 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c02 */

	select	max(qt_dia_exc_adiant)
	into STRICT	qt_dias_w
	from	parametro_contas_receber
	where	cd_estabelecimento = cd_estabelecimento_w;

	if (coalesce(qt_dias_w,0) > 0) then

		open	C01;
		loop
		fetch	C01 into
			nr_adiantamento_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

			begin
			delete	FROM adiantamento_hist
			where	nr_adiantamento = nr_adiantamento_w;

			delete	FROM adiantamento
			where	nr_adiantamento	= nr_adiantamento_w;
			exception
			when	others then
				rollback;
			end;

			commit;

		end	loop;
		close	C01;
	end if;

end	loop;
close	c02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_adiantamentos (cd_estabelecimento_p bigint) FROM PUBLIC;

