CREATE OR REPLACE PROCEDURE excluir_agendas_repetidas (nr_sequencia_p bigint) AS $body$
DECLARE


nr_minuto_duracao_w	bigint;
nr_seq_tipo_w			bigint;
ds_agenda_w				varchar(100);
dt_agenda_w				varchar(30);
nr_sequencia_w			bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_tasy
	where	nr_minuto_duracao = nr_minuto_duracao_w
	and	nr_seq_tipo			= nr_seq_tipo_w
	and	((ds_agenda = ds_agenda_w) or ((coalesce(ds_agenda::text, '') = '') and (coalesce(ds_agenda_w::text, '') = '')))
	and	to_char(dt_agenda,'hh24:mi') = dt_agenda_w
	and	dt_agenda > clock_timestamp();


BEGIN

select	nr_minuto_duracao,
	nr_seq_tipo,
	ds_agenda,
	to_char(dt_agenda,'hh24:mi')
into STRICT	nr_minuto_duracao_w,
	nr_seq_tipo_w,
	ds_agenda_w,
	dt_agenda_w
from	agenda_tasy
where	nr_sequencia = nr_sequencia_p;


open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	delete from	agenda_tasy_convite
	where 		nr_seq_agenda = nr_sequencia_w;

	delete from 	agenda_tasy_recurso
	where 		nr_seq_agenda = nr_sequencia_w;

	delete from	agenda_tasy
	where		nr_sequencia = nr_sequencia_w;

end loop;
close c01;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
