SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_anamnese_nao_lib ( qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE


cd_evolucao_w		bigint;
NR_SEQUENCIA_w		bigint;

c010 CURSOR FOR
	SELECT	NR_SEQUENCIA,
		DT_ANANMESE,
		CD_PESSOA_FISICA,
		NR_ATENDIMENTO
	from	anamnese_paciente
	where	DT_ANANMESE < (dt_parametro_p - (qt_hora_adicional_p / 24))
	  and	coalesce(dt_liberacao::text, '') = '';

c010_w	c010%rowtype;

BEGIN

OPEN C010;
LOOP
FETCH C010 into
	C010_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN
	NR_SEQUENCIA_w	:= c010_w.NR_SEQUENCIA;
	begin
	delete from anamnese_paciente
	where	NR_SEQUENCIA = NR_SEQUENCIA_w;
	exception
		when others then
			cd_evolucao_w := cd_evolucao_w;
	end;

	CALL gravar_log_exclusao('ANAMNESE_PACIENTE','JOB',
			' NR_SEQUENCIA : ' || NR_SEQUENCIA_W ||
			' DT_ANANMESE : ' || to_char(c010_w.DT_ANANMESE,'dd/mm/yyyy hh24:mi:ss') ||
			' CD_PESSOA_FISICA : ' ||c010_w.CD_PESSOA_FISICA ||
			' NR_ATENDIMENTO : '||c010_w.NR_ATENDIMENTO
			,'N');

	END;
END LOOP;
close c010;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
