CREATE OR REPLACE PROCEDURE excluir_atendimentos ( CD_ESTABELECIMENTO_P bigint, NR_ATENDIMENTO_INICIAL_P bigint, NR_ATENDIMENTO_FINAL_P bigint, NM_USUARIO_P text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_atendimento 
	from	atendimento_paciente 
	where	cd_estabelecimento = cd_estabelecimento_p 
	and	dt_alta < clock_timestamp() - interval '365 days' 
	and	nr_atendimento between nr_atendimento_inicial_p and nr_atendimento_final_p;

ds_erro_w	varchar(255);


BEGIN 
 
open C01;
loop 
fetch C01 into nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_erro_w := excluir_atendimento(nr_atendimento_w, 'N', nm_usuario_p, null);
	commit;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
