CREATE OR REPLACE PROCEDURE excluir_cadastro_item_suep ( nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE
 					

nr_seq_subitem_old_w bigint;
	
C02 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_item
	from	item_suep
	where	nr_sequencia = 	nr_seq_item_p;
	
C03 CURSOR FOR
	SELECT	nr_sequencia
	from	informacao_suep
	where	nr_seq_item = nr_seq_item_p;
	
C04 CURSOR FOR

	SELECT	nr_sequencia

	from	subitem_suep

	where	nr_seq_item = nr_seq_item_p;	

  
  C05 CURSOR FOR

	SELECT	nr_sequencia

	from	informacao_suep

	where	nr_seq_subitem = nr_seq_subitem_old_w;	
	
c02_w c02%rowtype;
c03_w c03%rowtype;
c04_w c04%rowtype;
c05_w c05%rowtype;
					

BEGIN
	
	open C02;
	loop
	fetch C02 into	
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
					
		open C03;
		loop
		fetch C03 into	
			c03_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin
			
			delete from informacao_suep where nr_seq_item = nr_seq_item_p;
      CALL gravar_log_exclusao('INFORMACAO ITEM SUEP', nm_usuario_p, 'NR_SEQ_ITEM=' || nr_seq_item_p, 'S');
			
			end;
		end loop;
		close C03;	

        open C04;

	     loop

	     fetch C04 into	

		c04_w;

	    EXIT WHEN NOT FOUND; /* apply on C04 */

		begin		

		nr_seq_subitem_old_w	:= c04_w.nr_sequencia;
		
		
     open C05;

		loop

		fetch C05 into	

			c05_w;

		EXIT WHEN NOT FOUND; /* apply on C05 */

			begin
			
          delete from informacao_suep where nr_seq_subitem = nr_seq_subitem_old_w;
          CALL gravar_log_exclusao('INFORMACAO SUBITEM SUEP', nm_usuario_p, 'NR_SEQ_SUBITEM=' || nr_seq_subitem_old_w, 'S');

			end;

		end loop;

		close C05;
		
		delete from subitem_suep where nr_sequencia = nr_seq_subitem_old_w;
    CALL gravar_log_exclusao('SUBITEM SUEP', nm_usuario_p, 'NR_SEQUENCIA=' || nr_seq_subitem_old_w, 'S');

    end;	

    end loop;

		close C04;	
		
		delete from item_suep where nr_sequencia = nr_seq_item_p;
    CALL gravar_log_exclusao('ITEM SUEP', nm_usuario_p, 'NR_SEQUENCIA=' || nr_seq_item_p, 'S');
		
		end;
	end loop;
	close C02;

commit;

end;
$body$
