SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_cig_atendimento (nr_atendimento_p bigint, nr_seq_glicemia_p bigint, nr_cig_atend_p bigint, nr_seq_medicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_medicao_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_glicemia_p IS NOT NULL AND nr_seq_glicemia_p::text <> '') and (nr_cig_atend_p IS NOT NULL AND nr_cig_atend_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	/* excluir cig completo */

	if (coalesce(nr_seq_medicao_p,0) = 0) then
		/* excluir cig */

		delete
		from	atendimento_cig
		where	nr_atendimento = nr_atendimento_p
		and	nr_cig_atend = nr_cig_atend_p;
	else
		/* gerar log exclusao */

		insert into atendimento_cig_exc(
							nr_sequencia,
							nr_cig_atend,
							nr_cig,
							nr_atendimento,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							dt_controle,
							qt_hgt,
							qt_variacao_hgt,
							qt_bolus_calc,
							qt_var_hgt_ref,
							qt_bolus_adm,
							qt_veloc_calc,
							qt_veloc_adm,
							qt_glicose_calc,
							qt_glicose_adm,
							ds_sugestao,
							ds_observacao,
							dt_proximo_controle,
							dt_prev_controle,
							ie_status_cig,
							qt_min_var,
							qt_min_interv_fut,
							dt_limite_ant,
							dt_limite_pos
							)
						SELECT	nextval('atendimento_cig_exc_seq'),
							nr_cig_atend,
							nr_cig,
							nr_atendimento,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							clock_timestamp(),
							nm_usuario_p,
							dt_controle,
							qt_hgt,
							qt_variacao_hgt,
							qt_bolus_calc,
							qt_var_hgt_ref,
							qt_bolus_adm,
							qt_veloc_calc,
							qt_veloc_adm,
							qt_glicose_calc,
							qt_glicose_adm,
							ds_sugestao,
							ds_observacao,
							dt_proximo_controle,
							dt_prev_controle,
							ie_status_cig,
							qt_min_var,
							qt_min_interv_fut,
							dt_limite_ant,
							dt_limite_pos
						from	atendimento_cig
						where	nr_atendimento = nr_atendimento_p
						and	nr_cig_atend = nr_cig_atend_p
						and	nr_sequencia = nr_seq_medicao_p;

		/* excluir cig */

		delete
		from	atendimento_cig
		where	nr_atendimento = nr_atendimento_p
		and	nr_cig_atend = nr_cig_atend_p
		and	nr_sequencia = nr_seq_medicao_p;

		/* validar status glicemia atendimento */

		if (coalesce(nr_seq_glicemia_p,0) > 0) then
			select	count(*)
			into STRICT	qt_medicao_w
			from	atendimento_cig
			where	nr_seq_glicemia = nr_seq_glicemia_p;

			if (qt_medicao_w = 0) then
				update	atend_glicemia
				set	ie_status_glic	= 'P',
					dt_inicio_glic	 = NULL,
					cd_pf_inic_glic	 = NULL
				where	nr_sequencia	= nr_seq_glicemia_p;
			end if;
		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_cig_atendimento (nr_atendimento_p bigint, nr_seq_glicemia_p bigint, nr_cig_atend_p bigint, nr_seq_medicao_p bigint, nm_usuario_p text) FROM PUBLIC;

