SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_cobranca_lote ( nr_sequencia_p text, ie_tipo_p text, nm_usuario_p text, ie_tipo_lote_p text) AS $body$
DECLARE


nr_titulo_w 		titulo_receber_orgao_cobr.nr_titulo%type;
nr_seq_cheque_w		cheque_cr_orgao_cobr.nr_seq_cheque%type;
ds_historico_w		varchar(255);
nr_seq_lote_tit_w	titulo_receber_orgao_cobr.nr_seq_lote%type;
nr_seq_lote_cheque_w	cheque_cr_orgao_cobr.nr_seq_lote%type;
nr_seq_lote_outros_w	outros_orgao_cobr.nr_seq_lote%type;
vl_acobrar_w		cobranca.vl_acobrar%type;
dt_aprovacao_w		timestamp;


BEGIN

if (ie_tipo_p = 'T') then

	select	nr_titulo,
		coalesce(nr_seq_lote,nr_seq_lote_exc)
	into STRICT 	nr_titulo_w,
		nr_seq_lote_tit_w
	from	titulo_receber_orgao_cobr
	where	nr_sequencia = nr_sequencia_p;

	select 	dt_aprovacao
	into STRICT	dt_aprovacao_w
	from 	lote_orgao_cobranca
	where	nr_sequencia = nr_seq_lote_tit_w;

	if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(675005);
	end if;

	if (ie_tipo_lote_p = 'I') then
		update	titulo_receber_orgao_cobr
		set	nr_seq_regra  = NULL,
			nr_Seq_lote  = NULL
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_lote_p = 'E') then
		update	titulo_receber_orgao_cobr
		set	nr_seq_regra  = NULL,
			nr_Seq_lote_exc  = NULL
		where	nr_sequencia = nr_sequencia_p;
	end if;

	ds_historico_w := wheb_mensagem_pck.get_texto(337827, 'NM_USUARIO=' || nm_usuario_p || ';NR_TITULO=' || nr_titulo_w || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_tit_w,ds_historico_w,nm_usuario_p,'E');

elsif (ie_tipo_p = 'C') then

	select	nr_seq_cheque,
		coalesce(nr_seq_lote,nr_seq_lote_exc)
	into STRICT 	nr_seq_cheque_w,
		nr_seq_lote_cheque_w
	from	cheque_cr_orgao_cobr
	where	nr_sequencia = nr_sequencia_p;

	select 	dt_aprovacao
	into STRICT	dt_aprovacao_w
	from 	lote_orgao_cobranca
	where	nr_sequencia = nr_seq_lote_cheque_w;

	if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(675005);
	end if;

	if (ie_tipo_lote_p = 'I') then
		update	cheque_cr_orgao_cobr
		set	nr_seq_regra  = NULL,
			nr_Seq_lote  = NULL
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_lote_p = 'E') then
		update	cheque_cr_orgao_cobr
		set	nr_seq_regra  = NULL,
			nr_Seq_lote_exc  = NULL
		where	nr_sequencia = nr_sequencia_p;
	end if;

	ds_historico_w := wheb_mensagem_pck.get_texto(337826, 'NM_USUARIO=' || nm_usuario_p || ';NR_SEQ_CHEQUE=' || nr_seq_cheque_w ||  ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_cheque_w,ds_historico_w,nm_usuario_p,'E');

elsif (ie_tipo_p = 'O') then

	select	coalesce(a.nr_seq_lote,a.nr_seq_lote_exc),
		b.vl_acobrar
	into STRICT	nr_seq_lote_outros_w,
		vl_acobrar_w
	from	outros_orgao_cobr a,
		cobranca b
	where	a.nr_seq_cobranca = b.nr_sequencia
	and 	a.nr_sequencia = nr_sequencia_p;

	select 	dt_aprovacao
	into STRICT	dt_aprovacao_w
	from 	lote_orgao_cobranca
	where	nr_sequencia = nr_seq_lote_outros_w;

	if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(675005);
	end if;

	if (ie_tipo_lote_p = 'I') then
		update	outros_orgao_cobr
		set	nr_seq_regra  = NULL,
			nr_Seq_lote  = NULL
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_lote_p = 'E') then
		update	outros_orgao_cobr
		set	nr_seq_regra  = NULL,
			nr_Seq_lote_exc  = NULL
		where	nr_sequencia = nr_sequencia_p;
	end if;

	ds_historico_w := wheb_mensagem_pck.get_texto(337828, 'NM_USUARIO=' || nm_usuario_p ||  ';VL_TOTAL=' || vl_acobrar_w || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_outros_w,ds_historico_w,nm_usuario_p,'E');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
