SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_comunic_interna ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_chave_w		varchar(255);
dt_comunicado_w		timestamp;
ds_titulo_w		varchar(255);
nm_usuario_w		varchar(15);


BEGIN

select	dt_comunicado,
	substr(ds_titulo,1,255),
	nm_usuario
into STRICT	dt_comunicado_w,
	ds_titulo_w,
	nm_usuario_w
from	comunic_interna
where	nr_sequencia = nr_sequencia_p;

ds_chave_w	:= substr('NR_SEQUENCIA=' || to_char(nr_sequencia_p) ||
			', DT_COMUNICADO=' || PKG_DATE_FORMATERS.to_varchar(dt_comunicado_w, 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, nm_usuario_p) ||
			', NM_USUARIO=' || nm_usuario_w ||
			', DS_TITULO=' || substr(ds_titulo_w,1,180)
			,1,255);

CALL gravar_log_exclusao('COMUNIC_INTERNA',nm_usuario_p,ds_chave_w,'N');

delete from comunic_interna
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
