SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_conta_paciente (NR_INTERNO_CONTA_P bigint, NM_USUARIO_P text, ie_commit_p text) AS $body$
DECLARE

DT_ATUALIZACAO_W          timestamp         := clock_timestamp();

qt_registro_w		bigint;
qt_registro_cta_w	bigint;
qt_reg_laudo_sus_w	bigint;
quebra_w		varchar(10)	:= chr(13)||chr(10);

BEGIN
/* Excluir Conta do Paciente */

select	count(*)
into STRICT	qt_registro_w
from	conta_paciente_desconto
where	nr_interno_conta	= NR_INTERNO_CONTA_P;

if (qt_registro_w	> 0) then	
	-- A conta  #@NR_INTERNO_CONTA#@ possui desconto e nao pode ser excluida. Favor desfazer o desconto antes de efetuar este processo.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(202910,'NR_INTERNO_CONTA='||NR_INTERNO_CONTA_P);
end if;

select	count(*)
into STRICT	qt_registro_cta_w
from	cta_pendencia
where	nr_interno_conta	= NR_INTERNO_CONTA_P;

if (qt_registro_cta_w > 0) then	
	-- A conta  #@NR_INTERNO_CONTA#@ possui pendencias vinculadas. Verificar a funcao Gestao de Pendencias da conta paciente.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(202917,'NR_INTERNO_CONTA='||NR_INTERNO_CONTA_P);
end if;

select	count(*)
into STRICT	qt_reg_laudo_sus_w
from	sus_laudo_paciente
where	nr_interno_conta = nr_interno_conta_p;

if (qt_reg_laudo_sus_w > 0) then
	update	sus_laudo_paciente
	set	nr_interno_conta  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_interno_conta = nr_interno_conta_p;
end if;

CALL gerar_log_conpaci_etapa(null, nr_interno_conta_p, nm_usuario_p);

/* Desvincular deducao - caso opte por excluir a conta particular*/

update	conta_pac_ded_conv_item a
set		nr_seq_propaci_dest  = NULL,
		nr_seq_matpaci_dest	 = NULL
where	exists (SELECT	1
				from	conta_pac_deducao_conv x
				where	x.nr_seq_conta_des = nr_interno_conta_p
				and		x.nr_sequencia	= a.nr_seq_deducao_conv);

update	conta_pac_deducao_conv
set		nr_seq_conta_des  = NULL
where	nr_seq_conta_des = NR_INTERNO_CONTA_P;
/* Fim desvincular deducao */

UPDATE PROCEDIMENTO_PACIENTE
   SET DT_ACERTO_CONTA    	 = NULL,
	 NR_INTERNO_CONTA		 = NULL
 WHERE NR_INTERNO_CONTA     	= NR_INTERNO_CONTA_P;

UPDATE MATERIAL_ATEND_PACIENTE
   SET DT_ACERTO_CONTA    	 = NULL,
	 NR_INTERNO_CONTA		 = NULL
 WHERE NR_INTERNO_CONTA     	= NR_INTERNO_CONTA_P;

CALL valida_item_repasse(nr_interno_conta_p);

delete	from sus_protocolo_envio_conta
where	nr_interno_conta = nr_interno_conta_p;

DELETE FROM CONTA_PACIENTE
WHERE NR_INTERNO_CONTA    	= NR_INTERNO_CONTA_P;

if (ie_commit_p	= 'S') then
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_conta_paciente (NR_INTERNO_CONTA_P bigint, NM_USUARIO_P text, ie_commit_p text) FROM PUBLIC;

