CREATE OR REPLACE PROCEDURE excluir_dem_integracao_grg (nr_seq_item_p bigint) AS $body$
DECLARE


nr_sequencia_dem_w numeric(20);


BEGIN

    if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

        select max(a.nr_sequencia) as NR_SEQUENCIA
            into STRICT nr_sequencia_dem_w
            from	imp_resp_recurso a,
                imp_resp_recurso_prot b,
                imp_resp_recurso_guia c,
                imp_resp_recurso_item d
            where a.nr_sequencia = b.nr_seq_resp_recurso
                and b.nr_sequencia = c.nr_seq_res_rec_prot
                and c.nr_sequencia = d.nr_seq_res_rec_guia
                and d.nr_sequencia = nr_seq_item_p;

        if (nr_sequencia_dem_w IS NOT NULL AND nr_sequencia_dem_w::text <> '') then
            delete from imp_resp_recurso
            where nr_sequencia = nr_sequencia_dem_w;
            commit;
        end if;

    end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
