SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_desvincula_autor (nr_seq_autor_p autorizacao_convenio.nr_sequencia%type, ie_desvincular_p text default 'N') AS $body$
DECLARE


procedimentos CURSOR FOR
SELECT	nr_sequencia
from	procedimento_autorizado
where	nr_sequencia_autor = nr_seq_autor_p;

materiais CURSOR FOR
SELECT	nr_sequencia
from	material_autorizado
where	nr_sequencia_autor = nr_seq_autor_p;	

BEGIN

	if	coalesce(ie_desvincular_p, 'N') = 'S' then
		for procedimento in procedimentos loop
			CALL DESVINCULAR_CONTA_PROC(procedimento.nr_sequencia);
		end loop;
		for material in materiais loop
			CALL DESVINCULAR_CONTA_MAT(material.nr_sequencia);
		end loop;
	end if;
	
	delete from autorizacao_convenio where nr_sequencia = nr_seq_autor_p;
	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
