SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_diluicao_medicamento ( nr_prescricao_p bigint, nr_sequencia_p bigint) AS $body$
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	
	delete	FROM prescr_material
	where	nr_prescricao			= nr_prescricao_p
    and		nr_sequencia_diluicao	= nr_sequencia_p
    and		ie_agrupador			in (
		3, -- Diluição
		7, -- Rediluição
		9  -- Reconstituição
	);
	
	update	prescr_material
	set     qt_hora_aplicacao		 = NULL,
	          qt_min_aplicacao		 = NULL, 
	          qt_solucao			 = NULL
    	where   nr_prescricao		= nr_prescricao_p
    	and     nr_sequencia		= nr_sequencia_p;
	
	delete	
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_kit not in (SELECT 	b.nr_sequencia
				 from	prescr_material b
				 where	b.nr_prescricao = nr_prescricao_p);
	
	end;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
