CREATE OR REPLACE PROCEDURE excluir_escala ( nr_seq_classif_p bigint, nr_seq_grupo_p bigint, nr_seq_escala_p bigint, nr_seq_escala_horario_p bigint, nr_seq_escala_diaria_p bigint, nr_seq_escala_diaria_adic_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_itens_w	integer;
nr_seq_escala_w	bigint;
nr_seq_grupo_w	bigint;

c01 CURSOR FOR
	SELECT 	b.nr_sequencia,
		a.nr_sequencia
	from	escala_grupo a,
		escala b
	where	a.nr_sequencia 		= b.nr_seq_grupo
	and	a.nr_seq_classif 	= nr_seq_classif_p;


c02 CURSOR FOR
	SELECT 	b.nr_sequencia
	from	escala b
	where	b.nr_seq_grupo		= nr_seq_grupo_p;


BEGIN

if (nr_seq_classif_p > 0) then

	select 	count(*)
	into STRICT	qt_itens_w
	from	agenda_paciente_escala a,
		escala b,
		escala_grupo c
	where	a.nr_seq_escala = b.nr_sequencia
	and	b.nr_seq_grupo	= c.nr_sequencia
	and	nr_seq_classif 	= nr_seq_classif_p;

	if (qt_itens_w = 0) then

		delete 	FROM escala_profissional
		where	nr_seq_classif = nr_seq_classif_p;

		open c01;
		loop
		fetch c01 into 	nr_seq_escala_w,
				nr_seq_grupo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			delete 	FROM escala_grupo_agenda
			where	nr_seq_grupo = nr_seq_grupo_w;

			delete	FROM escala_horario
			where	nr_seq_escala = nr_seq_escala_w;

			delete 	FROM escala_diaria
			where	nr_seq_escala = nr_seq_escala_w;

			delete	FROM escala_perfil
			where	nr_seq_escala = nr_seq_escala_w;

			delete	FROM escala_estab
			where	nr_seq_escala =  nr_seq_escala_w;

			delete	FROM escala_usuario
			where	nr_seq_escala =  nr_seq_escala_w;

			delete	FROM escala
			where	nr_sequencia = nr_seq_escala_w;

			delete 	FROM escala_grupo
			where nr_sequencia = nr_seq_grupo_w;

			end;
		end loop;
		close c01;

		delete 	FROM escala_classif
		where 	nr_sequencia = nr_seq_classif_p;

	end if;
end if;


if (nr_seq_grupo_p > 0) then

	select 	count(*)
	into STRICT	qt_itens_w
	from	agenda_paciente_escala a,
		escala b
	where	a.nr_seq_escala = b.nr_sequencia
	and	b.nr_seq_grupo	= nr_seq_grupo_p;

	if (qt_itens_w = 0) then

		delete 	FROM escala_grupo_agenda
		where	nr_seq_grupo = nr_seq_grupo_p;

		open c02;
		loop
		fetch c02 into 	nr_seq_escala_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin

			delete	FROM escala_horario
			where	nr_seq_escala = nr_seq_escala_w;

			delete 	FROM escala_diaria
			where	nr_seq_escala = nr_seq_escala_w;

			delete	FROM escala_perfil
			where	nr_seq_escala = nr_seq_escala_w;

			delete	FROM escala_estab
			where	nr_seq_escala =  nr_seq_escala_w;

			delete	FROM escala_usuario
			where	nr_seq_escala =  nr_seq_escala_w;

			delete	FROM escala
			where	nr_sequencia = nr_seq_escala_w;

			end;
		end loop;
		close c02;

		delete 	FROM escala_grupo
		where nr_sequencia = nr_seq_grupo_p;

	end if;
end if;

if (nr_seq_escala_p > 0) then

	select 	count(*)
	into STRICT	qt_itens_w
	from	agenda_paciente_escala a
	where	a.nr_seq_escala = nr_seq_escala_p;

	if (qt_itens_w = 0) then

		delete	FROM escala_horario
		where	nr_seq_escala = nr_seq_escala_p;

		delete 	FROM escala_diaria
		where	nr_seq_escala = nr_seq_escala_p;

		delete	FROM escala_perfil
		where	nr_seq_escala = nr_seq_escala_p;

		delete	FROM escala_estab
		where	nr_seq_escala = nr_seq_escala_p;

		delete	FROM escala_usuario
		where	nr_seq_escala =  nr_seq_escala_p;

		delete	FROM escala
		where	nr_sequencia = nr_seq_escala_p;

	end if;
end if;

if (nr_seq_escala_horario_p > 0) then

	select 	count(*)
	into STRICT	qt_itens_w
	from	agenda_paciente_escala a,
		escala_horario b
	where	b.nr_Seq_escala = a.nr_seq_escala
	and	b.nr_sequencia  = nr_seq_escala_horario_p;

	if (qt_itens_w = 0) then

		delete FROM escala_horario
		where nr_sequencia = nr_seq_escala_horario_p;

	end if;
end if;

if (nr_seq_escala_diaria_p > 0) then

	select 	count(*)
	into STRICT	qt_itens_w
	from	agenda_paciente_escala a,
		escala_diaria b
	where	b.nr_Seq_escala = a.nr_seq_escala
	and	b.nr_sequencia  = nr_seq_escala_diaria_p
	and	trunc(b.dt_inicio,'dd')	= trunc(a.dt_atualizacao,'dd');

	if (qt_itens_w = 0) then

		delete FROM escala_diaria
		where nr_sequencia = nr_seq_escala_diaria_p;

	end if;
end if;


if (nr_seq_escala_diaria_adic_p > 0) then

	select 	count(*)
	into STRICT	qt_itens_w
	from	agenda_paciente_escala a,
		escala_diaria_adic c,
		escala_diaria b
	where	c.nr_seq_escala_diaria 	= b.nr_sequencia
	and	b.nr_Seq_escala 	= a.nr_seq_escala
	and	c.nr_sequencia 		= nr_seq_escala_diaria_adic_p
	AND	c.cd_pessoa_fisica	= a.cd_anestesista
	and	trunc(b.dt_inicio,'dd')	= trunc(a.dt_atualizacao,'dd');


	if (qt_itens_w = 0) then

		delete FROM escala_diaria_adic
		where nr_sequencia = nr_seq_escala_diaria_adic_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_escala ( nr_seq_classif_p bigint, nr_seq_grupo_p bigint, nr_seq_escala_p bigint, nr_seq_escala_horario_p bigint, nr_seq_escala_diaria_p bigint, nr_seq_escala_diaria_adic_p bigint, nm_usuario_p text) FROM PUBLIC;

