CREATE OR REPLACE PROCEDURE excluir_estrutura_inventario ( nr_sequencia_p bigint, cd_grupo_mat_p bigint, cd_subgrupo_mat_p bigint, cd_classe_mat_p bigint) AS $body$
DECLARE


cd_material_w		integer;

c01 CURSOR FOR
SELECT	a.cd_material
from	inventario b,
	inventario_material a,
	estrutura_material_v x
where	b.nr_sequencia 		= a.nr_seq_inventario
and	x.cd_material  	 	= a.cd_material
and	nr_seq_inventario 		= nr_sequencia_p
and	coalesce(b.dt_bloqueio::text, '') = ''
and (x.cd_grupo_material    	= coalesce(cd_grupo_mat_p,0) or
	x.cd_subgrupo_material  	= coalesce(cd_subgrupo_mat_p,0) or
	x.cd_classe_material    	= coalesce(cd_classe_mat_p,0));

BEGIN

open c01;
loop
fetch c01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	delete
	from 	inventario_material
	where	nr_seq_inventario	= nr_sequencia_p
	and	cd_material	= cd_material_w;
	commit;
	end;
end loop;
close c01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
