CREATE OR REPLACE PROCEDURE excluir_etapa_sem_vinc_gedipa ( nr_seq_etapa_p bigint ) AS $body$
DECLARE


qt_vinculo_w	smallint;


BEGIN

select	count(*)
into STRICT	qt_vinculo_w
from	gedipa_etapa_hor
where	nr_seq_etapa_gedipa = nr_seq_etapa_p;

if (qt_vinculo_w = 0) then

	delete from gedipa_etapa_producao where nr_sequencia = nr_seq_etapa_p;
	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
