CREATE OR REPLACE PROCEDURE excluir_evolucao_nao_lib_estab ( cd_estabelecimento_p bigint, qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE

 
cd_evolucao_w		bigint;

c010 CURSOR FOR 
	SELECT	 /*+INDEX (a EVOPACI_I5) */ 
		distinct a.cd_evolucao 
	from	atendimento_paciente b, 
		evolucao_paciente a 
	where	a.dt_evolucao < (dt_parametro_p - qt_hora_adicional_p / 24) 
	and	a.nr_atendimento	= b.nr_atendimento 
	and	to_char(b.cd_estabelecimento)	= to_char(cd_estabelecimento_p) 
	and	coalesce(a.dt_liberacao::text, '') = '' 
	and	a.ie_evolucao_clinica <> 'AE';


BEGIN 
 
OPEN C010;
LOOP 
FETCH C010 into	 
	cd_evolucao_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN 
	 
	begin 
	delete from evolucao_paciente 
	where	cd_evolucao = cd_evolucao_w;
	exception 
		when others then 
			cd_evolucao_w := cd_evolucao_w;			
	end;
	CALL gravar_log_exclusao('EVOLUCAO_PACIENTE','JOB_ESTAB', 
			'CD_EVOLUCAO : ' || cd_evolucao_w ||obter_desc_expressao(330299)/*' Estabelecimento: '*/||to_char(cd_estabelecimento_p),'N');
	END;
END LOOP;
close c010;
 
commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
