CREATE OR REPLACE PROCEDURE excluir_fotos_cv_pf (qt_dias_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	bigint;
nr_sequencia_w		bigint;
cont_w			bigint := 0;
cd_pessoa_w		bigint;
count_w			bigint := 0;

c01 CURSOR FOR

SELECT  coalesce(b.cd_pessoa_fisica,0),
	a.nr_sequencia
from 	atendimento_visita_foto a,
	atendimento_visita b
where	a.nr_seq_atend_visita = b.nr_sequencia
and	a.dt_atualizacao <= clock_timestamp() - qt_dias_p
or	((coalesce(a.dt_atualizacao::text, '') = '') and (a.dt_atualizacao_nrec <= clock_timestamp() - qt_dias_p));

c02 CURSOR FOR

SELECT 	coalesce(b.cd_pessoa_fisica,0)
from	pessoa_fisica_foto a,
	pessoa_fisica b
where 	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and	b.cd_estabelecimento = cd_estabelecimento_p
and	a.dt_atualizacao <= clock_timestamp() - qt_dias_p
or	((coalesce(a.dt_atualizacao::text, '') = '') and (a.dt_atualizacao_nrec <= clock_timestamp() - qt_dias_p));


BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	/* insert into  log_xxxxxxtasy(
		dt_atualizacao,
		nm_usuario,
		cd_log,
		ds_log)
	values
		(
		sysdate,
		'TASY',
		55909,
		'Exclusão da foto da pessoa fisica: ' || cd_pessoa_fisica_w || ', da função : "Controle de Visita"'); */
	delete
	from 	ATENDIMENTO_VISITA_FOTO
	where	nr_sequencia = nr_sequencia_w;

	cont_w	:= cont_w + 1;

	if (cont_w = 1000) then
		commit;
		cont_w	:= 0;
	end if;

end loop;
close C01;

open C02;
loop
fetch C02 into
	cd_pessoa_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

 /*	insert into  logXXXX_tasy(
		dt_atualizacao,
		nm_usuario,
		cd_log,
		ds_log)
	values
	(
		sysdate,
		'TASY',
		55910,
		'Exclusão da foto da pessoa fisica: ' || cd_pessoa_w || ', da função : "Cadastro de Pessoa Física"');

		*/
	delete
	from	pessoa_fisica_foto
	where 	cd_pessoa_fisica = cd_pessoa_w;

	count_w	:= count_w + 1;

	if (count_w = 1000) then
		commit;
		count_w	:= 0;
	end if;

	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
