SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_fracionamento_processo (nr_seq_processo_p bigint) AS $body$
DECLARE


nr_seq_fracionamento_w		bigint;
cd_material_w			prescr_mat_hor.cd_material%type;
nr_seq_material_w		integer;
ie_agrupador_w			prescr_mat_hor.ie_agrupador%type;
nr_seq_pmh_w			bigint;
nr_prescricao_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_etiqueta
from	adep_regra_area_prep b,
		prescr_mat_hor a
where	a.nr_seq_processo		= nr_seq_processo_p
and	a.nr_seq_regra_area_prep	= b.nr_sequencia
and	coalesce(a.nr_seq_superior::text, '') = ''
and	a.ie_agrupador		not in (4)
and	coalesce(b.ie_gerar_manip_frac,'N')	= 'S'
and	coalesce(b.ie_situacao,'A')		= 'A'
and	coalesce(b.ie_exclusao,'N')		= 'N'
and	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S'
and (a.nr_seq_etiqueta IS NOT NULL AND a.nr_seq_etiqueta::text <> '')
order by
	a.nr_sequencia;


BEGIN

if (coalesce(nr_seq_processo_p,0) > 0) then

	open c01;
	loop
	fetch c01 into
			nr_seq_fracionamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update	prescr_mat_hor
		set		nr_seq_etiqueta	 = NULL
		where	nr_seq_processo	= nr_seq_processo_p
		and 	nr_seq_etiqueta	= nr_seq_fracionamento_w;

		delete FROM adep_processo_frac where nr_sequencia = nr_seq_fracionamento_w;

		end;
	end loop;
	close c01;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE excluir_fracionamento_processo (nr_seq_processo_p bigint) FROM PUBLIC;

