SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_ganho_perda_nao_lib ( qt_hora_adicional_p bigint, dt_parametro_p timestamp) AS $body$
DECLARE


nr_sequencia_w	 bigint;
dt_medida_w 	 timestamp;
nr_atendimento_w bigint;
dt_final_w		 timestamp;
dt_inicial_w	 timestamp;

c010 CURSOR FOR
	SELECT	nr_sequencia,
		    dt_medida,
			nr_atendimento
	  from	atendimento_perda_ganho
	 where  dt_medida between dt_inicial_w and dt_final_w
	   and	coalesce(dt_liberacao::text, '') = '';

c010_w	c010%rowtype;

BEGIN

dt_final_w		:= (dt_parametro_p - qt_hora_adicional_p / 24);
dt_inicial_w	:= dt_final_w - 30;

OPEN C010;
LOOP
FETCH C010 into
	nr_sequencia_w,
	dt_medida_w,
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C010 */
	BEGIN
	nr_sequencia_w := nr_sequencia_w;
	begin
	delete from atendimento_perda_ganho
	where  nr_sequencia = nr_sequencia_w;
	exception
		when others then
			nr_sequencia_w := nr_sequencia_w;
	end;

	CALL gravar_log_exclusao('ATENDIMENTO_PERDA_GANHO','JOB',
			' NR_SEQUENCIA : '   || nr_sequencia_w ||
			' DT_MEDIDA : '      || to_char(dt_medida_w,'dd/mm/yyyy hh24:mi:ss') ||
			' NR_ATENDIMENTO : ' || nr_atendimento_w
			,'N');

	END;
END LOOP;
close c010;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
