SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_glosa_valor_mat (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_status_acerto_w	smallint;
nr_sequencia_w		bigint;


BEGIN 
 
select coalesce(min(nr_sequencia),0) 
into STRICT nr_sequencia_w 
from material_atend_paciente 
where (nr_atendimento, dt_entrada_unidade, cd_material, dt_atendimento, cd_setor_atendimento) = 
	(SELECT nr_atendimento, dt_entrada_unidade, cd_material, dt_atendimento, cd_setor_atendimento 
	 from material_atend_paciente 
	 where nr_sequencia = nr_sequencia_p) 
 and nr_sequencia <> nr_sequencia_p 
 and qt_material = 0;
 
if (nr_sequencia_w <> 0) then 
	select coalesce(max(b.ie_status_acerto),0) 
	into STRICT ie_status_acerto_w 
	from conta_paciente b, 
		material_atend_paciente a 
	where a.nr_interno_conta = b.nr_interno_conta 
	 and a.nr_sequencia = nr_sequencia_w;
 
	RAISE NOTICE '% %', wheb_mensagem_pck.get_texto(793128), ie_status_acerto_w;
	if (ie_status_acerto_w = 1) then 
	RAISE NOTICE '%', wheb_mensagem_pck.get_texto(803395);
		delete FROM material_atend_paciente 
		where nr_sequencia = nr_sequencia_w;
		commit;
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
