CREATE OR REPLACE PROCEDURE excluir_grid_itens_prescr ( ie_origem_inf_p bigint, nr_sequencia_p bigint, nr_prescricao_p bigint) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	if (ie_origem_inf_p = 20) 	or (ie_origem_inf_p = 30) 	or (ie_origem_inf_p = 50) 	or (ie_origem_inf_p = 60) 	or (ie_origem_inf_p = 80) 	or (ie_origem_inf_p = 110) or (ie_origem_inf_p in (310,330)) then
		Delete 	from Prescr_material
		where 	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p in (70,90,120)) then
		Delete	from prescr_procedimento
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p = 100) then
		Delete	from prescr_gasoterapia
		where	nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p = 10) then
		Delete	from prescr_dieta
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p = 40) or (ie_origem_inf_p in (305,325)) then
		Delete	from prescr_solucao
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_solucao = nr_sequencia_p;
	elsif (ie_origem_inf_p = 130) then
		Delete	from prescr_recomendacao
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p in (31,270,280)) then
		Delete	from nut_pac
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p = 260) then
		Delete	from nut_paciente
		where	nr_prescricao = nr_prescricao_p
		and		nr_sequencia = nr_sequencia_p;
    elsif (ie_origem_inf_p = 290) then
		Delete	from rep_jejum
		where	nr_prescricao = nr_prescricao_p
		and		nr_sequencia = nr_sequencia_p;
	elsif (ie_origem_inf_p in (300,320)) then
		Delete	from hd_prescricao
		where	nr_prescricao = nr_prescricao_p
		and		nr_sequencia = nr_sequencia_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
