CREATE OR REPLACE PROCEDURE excluir_horario_lote_prescr_atx ( nr_seq_lote_p bigint, nr_seq_horario_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_itens_lote_w integer;
BEGIN
select 	count(*)
into STRICT	qt_itens_lote_w
from	ap_lote_item
where 	nr_seq_lote = nr_seq_lote_p
and 	nr_seq_mat_hor <> nr_seq_horario_p;

if (qt_itens_lote_w = 0) then
	
	update	ap_lote
	set 	ie_status_lote = 'C',
			nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_seq_lote_p;

end if;

commit;
exception
when others then
	CALL gravar_log_tasy(1812, 'excluir_horario_lote_prescr=' || to_char(sqlerrm), nr_seq_lote_p);
	
end;
$body$
