CREATE OR REPLACE PROCEDURE excluir_item_conta_kit ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_proc_mat_w		varchar(1);
nr_sequencia_w		material_atend_paciente.nr_sequencia%type;
nr_interno_conta_w	material_atend_paciente.nr_interno_conta%type;
cd_motivo_exc_conta_w	parametro_faturamento.cd_motivo_exc_conta%type;

c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		'M' ie_proc_mat, 
		a.nr_interno_conta 
	from	material_atend_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.ie_status_acerto	= 1 
	and	(a.cd_kit_material IS NOT NULL AND a.cd_kit_material::text <> '') 
	and	nr_seq_proc_princ	= nr_sequencia_p;


BEGIN 
 
select	max(cd_motivo_exc_conta) 
into STRICT	cd_motivo_exc_conta_w 
from	parametro_faturamento 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
open c01;
loop 
fetch c01 into 
	nr_sequencia_w, 
	ie_proc_mat_w, 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	CALL excluir_matproc_conta(nr_sequencia_w,nr_interno_conta_w,cd_motivo_exc_conta_w,null,ie_proc_mat_w,nm_usuario_p);
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
