CREATE OR REPLACE PROCEDURE excluir_item_conta_rla_cirurg ( nr_atendimento_p bigint, nr_cirurgia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
			 
ie_proc_mat_w		varchar(1);
nr_sequencia_w		bigint;	
nr_interno_conta_w	bigint;		
cd_motivo_exc_conta_w	bigint;
			
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		'P' ie_proc_mat, 
		a.nr_interno_conta 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.ie_status_acerto	= 1 
	and	(nr_seq_regra_lanc IS NOT NULL AND nr_seq_regra_lanc::text <> '') 
	and 	a.nr_cirurgia = nr_cirurgia_p 
	and 	a.nr_atendimento = nr_atendimento_p 
	
union all
 
	SELECT	nr_sequencia, 
		'M' ie_proc_mat, 
		a.nr_interno_conta 
	from	material_Atend_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.ie_status_acerto	= 1 
	and	(nr_seq_regra_lanc IS NOT NULL AND nr_seq_regra_lanc::text <> '') 
	and	nr_cirurgia_p	= nr_cirurgia_p 
	and 	a.nr_atendimento = nr_atendimento_p;
	

BEGIN 
 
select	max(cd_motivo_exc_conta) 
into STRICT	cd_motivo_exc_conta_w 
from	parametro_faturamento 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	ie_proc_mat_w, 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL excluir_matproc_conta(nr_sequencia_w,nr_interno_conta_w,cd_motivo_exc_conta_w, wheb_mensagem_pck.get_texto(305331), ie_proc_mat_w,nm_usuario_p);
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
