CREATE OR REPLACE PROCEDURE excluir_item_cot_compra ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_existe_w		bigint;
ds_erro_w		varchar(255);



BEGIN

select	count(*)
into STRICT	qt_existe_w
from	ordem_compra_item
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_cot_compra_p;

if (qt_existe_w > 0) then
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(280915);
else
	begin

	delete
	from	cot_compra_forn_item
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_item_cot_compra = nr_item_cot_compra_p;

	delete
	from	cot_compra_item
	where	nr_cot_compra = nr_cot_compra_p
	and	nr_item_cot_compra = nr_item_cot_compra_p;

	commit;

	end;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
