SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_item_lotes_agrupados ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_w	bigint;
ie_status_lote_w	varchar(1);


BEGIN

select	MAX(nr_seq_lote)
into STRICT	nr_seq_lote_w
from	ap_lote_agrup_item
where 	nr_sequencia = nr_sequencia_p;

delete from ap_lote_agrup_item
where nr_sequencia = nr_sequencia_p;

select	MAX(ie_status_lote)
into STRICT	ie_status_lote_w
from	ap_lote
where	nr_sequencia = nr_seq_lote_w;

if (coalesce(ie_status_lote_w,'A') <> 'C') then
	update	ap_lote
	set	ie_status_lote		= 'A',
		dt_disp_farmacia	 = NULL,
		nm_usuario_disp		 = NULL,
		ds_maquina_disp		 = NULL,
		cd_perfil_disp		 = NULL
	where	nr_sequencia		= nr_seq_lote_w;
else
	update	ap_lote
	set	dt_disp_farmacia	 = NULL,
		nm_usuario_disp		 = NULL,
		ds_maquina_disp		 = NULL,
		cd_perfil_disp		 = NULL
	where	nr_sequencia		= nr_seq_lote_w;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
