CREATE OR REPLACE PROCEDURE excluir_item_prescricao_agenda ( nr_prescricao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	/* Exclui os materiais da prescrição */

	delete from prescr_material where nr_prescricao = nr_prescricao_p;
	commit;

	/* Exclui os procedimentos da prescrição */

	delete from prescr_procedimento where nr_prescricao = nr_prescricao_p;
	commit;

	/* comentado por Alan OS204769
	Retira vínculo da prescrição com a agenda
	update	prescr_medica
	set	nr_seq_agenda	= null,
		dt_atualizacao	= sysdate,
		nm_usuario	= nm_usuario_p
	where	nr_prescricao	= nr_prescricao_p;
	*/
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
