CREATE OR REPLACE PROCEDURE excluir_item_processo_adep ( nr_seq_processo_p bigint, nr_seq_area_prep_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then
	delete
	from	adep_processo_evento a
	where	a.nr_seq_item in (	SELECT	b.nr_sequencia
					from	adep_processo_item b
