					and	((coalesce(nr_seq_area_prep_p::text, '') = '') or (b.nr_seq_area_prep = nr_seq_area_prep_p)));
	delete
	from	adep_processo_item
	where	nr_seq_processo	= nr_seq_processo_p
	and	((coalesce(nr_seq_area_prep_p::text, '') = '') or (nr_seq_area_prep = nr_seq_area_prep_p));

end if;

commit;

end;
