CREATE OR REPLACE PROCEDURE excluir_itens_adic_pacote ( ds_area_procedimento_p text, ds_especialidade_p text, ds_grupo_proc_p text, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
0 - √Årea procedimento
1 - Especialidade
2 - Grupo procedimento
*/
BEGIN

if (ie_opcao_p = 0) then
	delete	from w_copia_regra_pacote
	where	obter_se_contido(cd_area_procedimento, ds_area_procedimento_p) = 'S';
elsif (ie_opcao_p = 1) then
	delete	from w_copia_regra_pacote
	where	obter_se_contido(cd_especialidade, ds_especialidade_p) = 'S';
elsif (ie_opcao_p = 2) then
	delete	from w_copia_regra_pacote
	where	obter_se_contido(cd_grupo_proc, ds_grupo_proc_p) = 'S';
end if;

commit;

end;
