CREATE OR REPLACE PROCEDURE excluir_itens_exec_auditoria ( nr_seq_auditoria_p bigint, cd_motivo_exc_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
ie_proc_mat_w		varchar(1);
nr_seq_item_w		bigint;			
nr_interno_conta_w	bigint;
			
C01 CURSOR FOR 
	SELECT	'P', 
			a.nr_seq_propaci, 
			b.nr_interno_conta 
	from	auditoria_propaci a, 
			procedimento_paciente b 
	where	a.nr_seq_propaci		= b.nr_sequencia 
	and		nr_seq_auditoria		= nr_seq_auditoria_p 
	and		ie_tipo_auditoria		= 'E' 
	
union all
 
	SELECT	'M', 
			nr_seq_matpaci, 
			b.nr_interno_conta 
	from	auditoria_matpaci a, 
			material_atend_paciente b 
	where	a.nr_seq_matpaci 	= b.nr_sequencia 
	and		nr_seq_auditoria		= nr_seq_auditoria_p 
	and		ie_tipo_auditoria		= 'E' 
	order by 1;

BEGIN
 
if (cd_motivo_exc_conta_p IS NOT NULL AND cd_motivo_exc_conta_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		ie_proc_mat_w, 
		nr_seq_item_w, 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL excluir_matproc_conta(nr_seq_item_w,nr_interno_conta_w,cd_motivo_exc_conta_p,null,ie_proc_mat_w,nm_usuario_p);
		end;
	end loop;
	close C01;
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
