SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE excluir_itens_kit_prescr ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_sequencia_p prescr_material.nr_sequencia%type) AS $body$
DECLARE


ie_atualizado_w		boolean;


BEGIN
	ie_atualizado_w := false;

	if (coalesce(nr_sequencia_p,0) <> 0) then
		delete	
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and		nr_seq_kit = nr_sequencia_p;
		
		GET DIAGNOSTICS ie_atualizado_w = ROW_COUNT > 0;
	else
		delete	
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and		nr_seq_kit not in ( SELECT	b.nr_sequencia
									from	prescr_material b
									where	b.nr_prescricao = nr_prescricao_p);

		GET DIAGNOSTICS ie_atualizado_w = ROW_COUNT > 0;
	end if;

	if (ie_atualizado_w) then
		update	prescr_medica
		set		ie_gerou_kit  = NULL,
				dt_atualizacao = clock_timestamp(),
				ds_stack = substr(ds_stack || ' atualizado ie_gerou_kit',1,1800)
		where	nr_prescricao = nr_prescricao_p;

		/* update	prescr_material
		set	ie_kit_gerado = 's'
		where	nr_prescricao = nr_prescricao_p;
		and	nr_sequencia_diluicao is not null; */
	end if;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
