CREATE OR REPLACE PROCEDURE excluir_itens_kit_vinculado (nr_prescricao_p bigint, nr_seq_item_p bigint, nm_tabela_p text) AS $body$
DECLARE


ie_ctrl_w			varchar(1);
nr_sequencia_w		prescr_material.nr_sequencia%type;
nr_seq_kit_w		prescr_material.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and 	nr_sequencia_solucao = nr_seq_item_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_kit = nr_sequencia_w;


BEGIN

if (nm_tabela_p = 'PRESCR_SOLUCAO') then

	select  coalesce(max('S'),'N')
	into STRICT	ie_ctrl_w
	from 	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia_solucao = nr_seq_item_p;

	if (ie_ctrl_w = 'S') then

		open C01;
		loop
		fetch C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			select 	coalesce(max('S'),'N')
			into STRICT	ie_ctrl_w
			from 	prescr_material
			where 	nr_prescricao = nr_prescricao_p
			and 	nr_seq_kit = nr_sequencia_w;

			if (ie_ctrl_w = 'S') then

				open C02;
				loop
				fetch C02 into
					nr_seq_kit_w;
				EXIT WHEN NOT FOUND; /* apply on C02 */
					begin

						delete 	from prescr_material
						where 	nr_prescricao = nr_prescricao_p
						and 	nr_sequencia = nr_seq_kit_w;
					end;
				end loop;
				close C02;
			end if;
			end;
		end loop;
		close C01;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
