CREATE OR REPLACE PROCEDURE excluir_itens_nao_consistidos ( nr_prescricao_p bigint, nr_requisicao_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_material
	where	nr_prescricao 		= nr_prescricao_p
	and	ie_status_cirurgia  	= 'GI'
	and	cd_motivo_baixa = 0;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	delete	from prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_w
	and	cd_motivo_baixa = 0;
	commit;

	if (coalesce(nr_requisicao_p, 0) > 0) then
		begin
		update	mat_atend_pac_log
		set	nr_requisicao 	= nr_requisicao_p
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_mat	= nr_sequencia_w
		and	ie_acao		= 'E';
		commit;
		end;
	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
